import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [jobTitles, setJobTitles] = useState([]);
  const [selectedTitle, setSelectedTitle] = useState('');
  const [recommendedJobs, setRecommendedJobs] = useState([]);

  useEffect(() => {
    // Fetch job titles from Flask backend when component mounts
    const fetchJobTitles = async () => {
      try {
        const response = await axios.get('http://localhost:5000/job_titles');
        setJobTitles(response.data);
      } catch (error) {
        console.error('Error fetching job titles:', error);
      }
    };

    fetchJobTitles();
  }, []);

  const handleTitleChange = (event) => {
    setSelectedTitle(event.target.value);
  };

  const handleGetRecommendations = async () => {
    try {
      const response = await axios.post('http://localhost:5000/recommend', { title: selectedTitle });
      setRecommendedJobs(response.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  return (
    <div>
      <h1>Job Recommendation System</h1>
      <select value={selectedTitle} onChange={handleTitleChange}>
        <option value="">Select a job title</option>
        {jobTitles.map((title) => (
          <option key={title} value={title}>
            {title}
          </option>
        ))}
      </select>
      <button onClick={handleGetRecommendations}>Get Recommendations</button>
      <h2>Recommended Jobs</h2>
      <ul>
        {recommendedJobs.map((job, index) => (
          <li key={index}>{job}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
